import SHOP_DATA from "./shop.data.js";

import { updateCollections } from "./shop.actions.js";

const INITIAL_STATE = {
  collections: SHOP_DATA
};

const shopReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    default:
      return state;
  }
};

export default shopReducer;
